MATCH (e1:ETF {code: '069500'})-[:HOLDS]->(s:Stock)<-[:HOLDS]-(e2:ETF {code: '148020'})
WITH count(DISTINCT s) as common_count
RETURN {common_stock_count: common_count}

MATCH (e:ETF)-[:TAGGED]->(t:Tag {name: 'AI'})
RETURN {etf_code: e.code, etf_name: e.name, expense_ratio: e.expense_ratio}
ORDER BY e.expense_ratio ASC

