MATCH (u:User {user_id: $user_id})-[w:WATCHES]->(e:ETF)
RETURN {etf_code: e.code, etf_name: e.name, added_at: w.added_at}
ORDER BY w.added_at DESC

MATCH (u:User {user_id: $user_id})-[:WATCHES]->(e:ETF)
RETURN {etf_code: e.code, etf_name: e.name, return_1d: e.return_1d, return_1w: e.return_1w, return_1m: e.return_1m}
ORDER BY e.return_1m DESC

MATCH (u:User {user_id: $user_id})-[:WATCHES]->(e:ETF)
WITH count(e) as watch_count
RETURN {watch_count: watch_count}

MATCH (u:User {user_id: $user_id})-[:WATCHES]->(e:ETF)
WITH sum(e.net_assets) as total_assets
RETURN {total_net_assets: total_assets}

MATCH (e:ETF)-[:HAS_CHANGE]->(c:Change {change_type: 'new'})
RETURN {etf_code: e.code, etf_name: e.name, stock_name: c.stock_name, weight: c.after_weight, detected_at: c.detected_at}
ORDER BY c.detected_at DESC

MATCH (e:ETF)-[:HAS_CHANGE]->(c:Change {change_type: 'removed'})
RETURN {etf_code: e.code, etf_name: e.name, stock_name: c.stock_name, before_weight: c.before_weight, detected_at: c.detected_at}
ORDER BY c.detected_at DESC

MATCH (e:ETF)-[:TAGGED]->(t:Tag {name: '철강'})
RETURN {etf_code: e.code, etf_name: e.name, expense_ratio: e.expense_ratio}
ORDER BY e.expense_ratio ASC
